class MultipleChoiceOption extends tApp.Component{constructor(t,e){super(t,e)}render(t){return null!=this.parent.parent.state.data?`<div class="mc-answer mc-answer-${this.state.index}" onclick="{{_this}}.update();">
	<p>${tApp.escape(this.parent.parent.state.data.answers[this.state.index])}</p>
</div>`:"<div></div>"}update(){this.parent.parent.setState("data.selectedAnswer",this.state.index)}}module.exports=MultipleChoiceOption;