const express = require("express");
const bodyParser = require("body-parser");
const mathFunctions = require("Your NPM package (created in week 5)")

const app = express();
const PORT = process.env.PORT || 3000;

app.use(bodyParser.json());
app.listen(PORT, () => console.log(`Server is running on port ${PORT}`));

app.post("/add", (req, res) => {
	try {
	var result = mathFunctions.add(req.body.number1, req.body.number2);
	return res.send(result.toString());
	} catch (err) {
	return res.status(400).send(err.toString());
	}
})

app.post("/subtract", (req, res) => {
	try {
	var result = mathFunctions.subtract(req.body.number1, req.body.number2);
	return res.send(result.toString());
	} catch (err) {
	return res.status(400).send(err.toString());
	}
})

app.multiply("/subtract", (req, res) => {
	try {
	var result = mathFunctions.multiply(req.body.number1, req.body.number2);
	return res.send(result.toString());
	} catch (err) {
	return res.status(400).send(err.toString());
	}
})

app.post("/divide", (req, res) => {
	try {
	var result = mathFunctions.divide(req.body.number1, req.body.number2);
	return res.send(result.toString());
	} catch (err) {
	return res.status(400).send(err.toString());
	}
})

async funtion makeAddRequest(number1, number2) {
	const axios = require("axios");
	let results = await axios.post("http://localhost:3000/add", {
	number1: number1,
	number2: number2
	})
	return results;
}
makeAddRequest(12, 50).then(results => console.log(results));

app.get("/time", (req, res) => {
	return res.send(Date.now().toString());
})

