var value = "";
var cursor = 0;
...

function number(num) {
	value += num;
	cursor = 0;
	updateValue(value);
}
function del() {
	if(value == "ERROR") {
		clearVal();
	} else {
		value = value.substring(0, value.length - 1);
		updateValue(value);
	}
}
function clearVal() {
	value = "";
	updateValue(value);
}
function updateValue(val) {
	if(val.length > 11) {
		if(cursor != 0 && value.length - cursor != 11) {
			document.getElementById("value").innerHTML = "..." + val.substring(val.length - 11 - cursor, val.length - cursor) + "...";
		} else if(value.length - cursor != 11) {
			document.getElementById("value").innerHTML = "..." + val.substring(val.length - 11 - cursor, val.length - cursor);
		} else {
			document.getElementById("value").innerHTML = val.substring(val.length - 11 - cursor, val.length - cursor) + "...";
		}
	} else {
		document.getElementById("value").innerHTML = val;
	}
}
function front() {
	if(cursor > 0) {
		cursor--;
		updateValue(value);
	}
}

function back() {
	if(cursor < value.length - 11) {
		cursor++;
		updateValue(value);
	}
}

function calculate() {
	try {
		value = eval(value).toString();
		cursor = value.length - 11;
	} catch(err) {
		value = "ERROR";
	}
	updateValue(value);
}

@font-face {
	font-family: "Calculator";
	src: url("https://raw.githubusercontent.com/App-Dev-League/curriculum-hub-webdev/main/week4-project/Calculator.ttf")
}

html,
body {
	background-color: #2c3e50;
	width: 100%;
	height: 100%;
	font-family: "Calculator";
	margin: 0;
	padding: 0;
	overflow: hidden;
}

.calculator {
	box-shadow: 0 10px 30px 0 #000000;
	width: calc(100% - 30px);
	height: calc(100% - 30px);
	max-width: 400px;
	max-height: 600px;
	position: absolute;
	left: 50%;
	top: 50%;
	transform: translate(-50%, -50%);
}

.header {
	background-color: #eb3b5a;
	color: #ffffff;
	width: 100%;
	height: 30%;
}
.body {
	background-color: #2c3e50;
	width: 100%;
	height: 70%;
	font-size: 0;
}

#value {
	position: absolute;
	left: 50%;
	font-size: 65px;
	top: calc(15% - 50px);
	transform: translate(-50%, -50%);
}

@media (max-width: 400px) {
	#value {
	font-size: 50px;
	top: calc(15% - 35px);
	}
}

.calculator-button {
	font-family: sans-serif;
	margin: 0;
	border: 0;
	width: 25%;
	height: 20%;
	font-size: 30px;
	background-color: #2c3e50;
	transition: box-shadow 0.15s ease-in-out;
	cursor: pointer;
}

.calculator-button:focus {
	outline: 0;
}

.calculator-button:active {
	box-shadow: inset 0px 0px 20px 0px #232222;
}

.calculator-number {
	color: #eb3b5a;
}

.calculator-operation {
	color: #ffffff;
}

.calculator-extra {
	color: #7680a2;
}
